import React, { useEffect, useState } from 'react';
import { addVehicle, getSlots } from '../services/ApiService';

function VehicleForm() {
    const [number, setNumber] = useState('');
    const [type, setType] = useState('');
    const [slotId, setSlotId] = useState('');
    const [slots, setSlots] = useState([]);

    useEffect(() => {
        getSlots().then(res => setSlots(res.data.filter(s => !s.occupied)));
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        const selectedSlot = slots.find(s => s.id === parseInt(slotId));
        addVehicle({
            number,
            type,
            slot: selectedSlot
        }).then(() => {
            setNumber('');
            setType('');
            setSlotId('');
            window.location.reload();
        });
    };

    return (
        <div>
            <h2>Park New Vehicle</h2>
            <form onSubmit={handleSubmit}>
                <input
                    placeholder="Vehicle Number"
                    value={number}
                    onChange={e => setNumber(e.target.value)}
                    required
                />
                <input
                    placeholder="Vehicle Type"
                    value={type}
                    onChange={e => setType(e.target.value)}
                    required
                />
                <select value={slotId} onChange={e => setSlotId(e.target.value)} required>
                    <option value="">Select Slot</option>
                    {slots.map(s => (
                        <option key={s.id} value={s.id}>{s.slotNumber}</option>
                    ))}
                </select>
                <button type="submit">Park</button>
            </form>
        </div>
    );
}

export default VehicleForm;
